// ajax code for jquery
// version 1.0
// date: 2012-12-12


// ajax get
function jqaj_get(url, data, callback) {
    $.get(url, data, callback);
}

// ajax post
function jqaj_post(url, data, callback) {
    $.post(url, data, callback);
}

// ajax get json
function jqaj_get_json(url, data, callback) {
    $.getJSON(url, data, callback);
}

// ajax post json
function jqaj_post_json(url, data, callback) {
    $.postJSON(url, data, callback);
}

// ajax get xml
function jqaj_get_xml(url, data, callback) {
    $.getXML(url, data, callback);
}

